                st.push("[");
            }else if(s[i] == ']'){
                string word, current;
                while(st.top() != "["){
                    word = st.top() + word;
                    st.pop();
                }
                st.pop();
                int times = stoi(st.top());  st.pop();
                while(times--){
                    current += word;
                }
                st.push(current);
            }
        }
        while(!st.empty()){
            result = st.top() + result; 
            st.pop();
        }
        return result;
    }
};
