private String decodeStringHelper(String s, int[] index) {
	StringBuilder sb = new StringBuilder();
	String num = "";
	for (int i = index[0]; i < s.length(); i++) {
		char c = s.charAt(i);
		if (c != '[' && c != ']' && !Character.isDigit(c))
			sb.append(c);
		else if (Character.isDigit(c))
