                while(isdigit(s[itr])){
                    num = num * 10 + s[itr++] - '0';
                }
                intSt.push(num);
            }
            else if(s[itr] == ']'){
                int times = intSt.top();
                intSt.pop();

                string str = strSt.top();
                strSt.pop();

                for(int itr = 1; itr <= times; itr++){
                    str += res;
                }

                res = str;
                itr++;
            }
            else if(s[itr] == '['){
                strSt.push(res);
                res = "";
                itr++;
            }
            else{
                res += s[itr];
                itr++;
            }
        }
        return res;
    }
