                int count = stoi(times);

                while(count--){
                    for(int jtr = 0; jtr < str.size(); jtr++){
                        st.push(str[jtr]);
                    }
                }
            }
            else{
                st.push(s[itr]);
            }
        }

        string res = "";
        while(!st.empty()){
            res += st.top();
            st.pop();
        }
        reverse(res.begin(), res.end());
        return res;
    }
};
