class Solution {
public:
    string decodeString(string s) {
        stack<char>st;
        string ans = "";
        for(int i=0;i<s.size();i++){
            // Step1 
            if(s[i]!=']'){
                st.push(s[i]);
            }else{
                // Step2
                string temp = "";
                while(st.top()!='['){
                    temp += st.top();
                    st.pop();
                }
                st.pop();
                // Step3
                string num = "";
                while(!st.empty() && st.top()>='0' && st.top()<='9'){
                    num += st.top();
                    st.pop();
                }
                reverse(num.begin(),num.end());
                int n = stoi(num);
                // Step4
                for(int k=0;k<n;k++){
                    for(int j=temp.size()-1;j>=0;j--){
                        st.push(temp[j]);
                    }
                }
            }
        }
        // Step5 
        while(!st.empty()){
            ans += st.top();
            st.pop();
        }
        reverse(ans.begin(),ans.end());
        return ans;
    }
};
