class Solution {
    public String decodeString(String s) {
        Queue<Character> queue = new ArrayDeque<>();

        for(char c : s.toCharArray()) {
            queue.offer(c);
        }

        return dfs(queue);
    }

    public String dfs(Queue<Character> queue) {
        int num = 0;
        StringBuilder builder = new StringBuilder();

        while(!queue.isEmpty()) {
            char c = queue.poll();

            if('0' <= c && c <= '9') {
                num = num * 10 + (c-'0');
            } else if( c =='['){
                builder.append(dfs(queue).repeat(num));
                num = 0;
            } else {
                if( 'a' <= c && c <= 'z') {
                    builder.append(c);
                }

                if(c == ']') {
                    break;
                }
            }
        }
        return builder.toString();
    }
}
