class Solution {
    func decodeString(_ s: String) -> String {
        guard s.count > 0 else {
            return ""
        }

        //multiplier stack record k, the number of repeating times
        var multiplierStack = [Int]()

        //prefix stack record the substrings before the bracket
        var prefixStack = [String]()

        var numberString = ""
        var subString = ""

        //if the string start with charater, treat it as 1[...]
        if !s.first!.isNumber {
            multiplierStack.append(1)
        }

        for char in s {
            if char.isNumber {
				// -- In the case char is "number", save it by numberString, 
				// because number can be multiple digits
                numberString.append(char)
            } else if char == "[" {
				// -- In the case char is "[", we know we finish recoding the last 
				// digit of the Int, so we push it to stack as type of Int value, 
				// also push the subString to another stack as prefix
                multiplierStack.append(Int(numberString)!)
                prefixStack.append(subString)
                numberString = ""
                subString = ""
            } else if char == "]" {
				 // -- In the case char is "]", we know this is the end of a repeating
				// case, and there are prefix and mutiplier in the stack ready to use
				// and form the repeating chars.
                let multiplier = multiplierStack.removeLast()
                var temp = ""
                for index in 0..<multiplier {
                    temp.append(subString)
                }
                let prefix = prefixStack.removeLast()
                subString = prefix + temp
            } else {
				// -- In the case char is "character", save it by subString
                subString.append(char)
            }
        }

        return subString
    }
}
