class Solution {
public:
    string decodeString(string s) {
        stack <string> st;
        for(char c : s){
            //pushing all characters other than ']'
            if(c != ']') st.push(string(1,c));
            else{
                //finding string part 
                string str = "";
                while(st.top() != string(1,'[')){
                    str += st.top();
                    st.pop();
                }
                reverse(str.begin() , str.end());
                //finding number/integer part
                string temp = "";
                st.pop(); // poping '['
                while(!st.empty() && st.top() >= string(1,'0') && st.top() <= string(1,'9')){
                    temp += st.top();
                    st.pop();
                }
                reverse(temp.begin(),temp.end());
                int num = 0;
                if(temp.size()==0) num = 1;
                else num = stoll(temp);
