class Solution {
public:
    string decodeString(string s) {
        // go through the string, push all [ onto the stack, once you en//couter a ], pop off the [, map the index to the right index
        stack<int> st;
        unordered_map<int, int> leftToRight;

        for (int i = 0; i < s.size(); i++)
        {
            if (s[i] == '[')
            {
                st.push(i);
            }
            else if (s[i] == ']')
            {
                int left = st.top();
                st.pop();
                int right = i;
                leftToRight[left] = right;
            }
        }
        //cout << "calling decode on s: " << s << endl;
        // if you see a nonnumber push on
        // if you see a digit, find the first [, slice out the digit
        // then find the ], slice out the internal substring
        // recursive call on the internal substring
        // push the internal substring on however that many times
        string res;
        int currIndex = 0;
        int n = s.size();
        while (currIndex < n) // still processing
        {
            //cout << "   currIndex: " << currIndex << endl;
            if (!isdigit(s[currIndex]))
            {
                res += s[currIndex];
                currIndex++;
                //cout << "       safely pushed to res: " << res << endl;
            }
            else
            {
                // find the last instance of the digit, find the left[ and right]
                int lastDigitIndex = currIndex;
                //cout << "       found some digit, currIndex: " << currIndex << endl;
                while(s[lastDigitIndex] != '[')
                {
                    lastDigitIndex++;
                } // now: s[lastDigitIndex] = '['
                int leftBracketIndex = lastDigitIndex;
                lastDigitIndex--;
                int numRep = stoi(s.substr(currIndex, lastDigitIndex - currIndex + 1));
                int rightBracketIndex = leftToRight[leftBracketIndex];
                string innerString = s.substr(leftBracketIndex+1, (rightBracketIndex - 1 - (leftBracketIndex + 1) + 1));
                string innerDecoded = decodeString(innerString);

                for (int i = 0; i < numRep; i++)
                {
                    res += innerDecoded;
                }
                currIndex = rightBracketIndex + 1;
            }
        }
        return res;
    }
};
```

