        Stack<Integer> numStack = new Stack();
        Stack<String> strStack = new Stack();
        StringBuilder sb = new StringBuilder();
        int len = s.length();
        for(int i=0;i< len ; i++){
            char ch = s.charAt(i);
            if(Character.isDigit(ch)){
