class Solution {
public:

    string decodeString(string s) 
    {
        if(s.length()==1 && isalpha(s[0])){return s;}
        stack<char> st;
        string temp="",res="";
        int i=0,k_time=0;
        for(i=0;i<s.length();i++)
        {
            temp="";
            while(s[i]!=']' && i<s.length())
            {
                st.push(s[i]);
                i++;    
            }
            i++;
            temp="";
            while(st.top()!='[' && !st.empty())
            {
                temp+=st.top();
                st.pop();
            }

            st.pop();
            if(temp.length()>1)
            {
                reverse(temp.begin(),temp.end());
            }


            string num="";

            if(!st.empty())
            {
                num=num+st.top();
                st.pop();
                k_time= stoi(num);
            }


            while(k_time>0)
            {
                res = res + temp;
                k_time--;
            }    
        }
        reverse(res.begin(),res.end());
        return res;    
    }
};

