                reverse(toPush.begin(),toPush.end());
                st.push(toPush);
                check = 1;
            }
        }

        string ans="";
        while(!st.empty()){
            ans+= st.top();
            st.pop();
        }

        reverse(ans.begin(),ans.end());
        // if(!check)
        // return "";
        return ans;
    }
};
