        memset(dp,0,sizeof(dp));

        dp[0]=1; //unique bst with zero node is one
        dp[1]=1; //unique bst with one node is one
        for(int i=2;i<=n;i++){
            int left=0;
            int right=i-1;
            while(left<=i-1){
                dp[i]+= dp[left]*dp[right];
                left++;
                right--;
            }
        }
        return dp[n];
    }
