class Solution {
public:

    // SOLUTION 1 : RECURSIVE SOLUTION
    int solve(int n){
        // base case
        if(n==0 || n==1){
            return 1;
        }

        // recursive calls
        int ans = 0;
        for(int i = 0; i<n; i++){
            int temp = solve(i) * solve(n-i-1);
            ans += temp;
        }

        return ans;
    }

    // SOLUTION 2 : RECURSION + MEMOIZATION
    int solve2(int n, vector<int>&dp){
        // base case
        if(n <= 1){
            return 1;
        }

        // dp call
        if(dp[n-1] != -1){
            return dp[n-1];
        }

        // recursive calls
        int ans = 0;
        for(int i = 1; i<=n; i++){
            int temp = solve2(i-1, dp) * solve2(n-i, dp);
            ans += temp;
        }

        return dp[n-1] = ans;
    }

    // SOLUTION 3 : TABULATION METHOD
    int solveTab(int n){
        // creating dp vector
        vector<int>dp(n+1, 0);

        // base cases handling
        dp[0] = dp[1] = 1;

        // i --> no of nodes
        for(int i = 2; i<=n; i++){
            // j --> no of roots
            for(int j = 1; j<=i; j++){
                int temp = dp[j-1] * dp[i-j];
                dp[i] += temp;
            }
        }

        return dp[n];
    }


    int numTrees(int n) {
        // recursive solution
        // return solve(n);

        // recursive + memoization solution
        // vector<int>dp(n, -1);
        // return solve2(n, dp);

        // tabulation method
        return solveTab(n);
    }
};
```

