int numTrees( int n ) {
    if( n == 1 || n == 0 ) return 1;
    unordered_map<int, int> hash;
    hash[0] = 1;
    hash[1] = 1;

    int result = 0;
    for( int i=0; i < n; i++ ) {
        if( hash.find(i) == hash.end() )
            hash[i] = numTrees( i );
        if( hash.find( n-i-1 ) == hash.end() )
            hash[ n-i-1 ] = numTrees( n-i-1 );
        result += hash[i] * hash[ n - i - 1 ];   
    }
    return result;
}

