        for (int i=0; i<n; i++) 
            res += catalan(i,memo)*catalan(n-i-1,memo); 
        memo[n]=res;
        return res; 
    } 
    int numTrees(int n) {
        vector<long long int> memo(n+1,-1);
        return catalan(n,memo);
    }
};
