        for (int i = 1; i < A.length; i++) {
            f[i] = f[i - 1] - sum + A[i - 1] * n;
            if (f[i] > maxF) {
                maxF = f[i];
            }
        }

        return (int) maxF;
    }

    private int evaluate(int[] A) {
        int result = 0;
        for (int i = 0; i < A.length; i++) {
            result += i * A[i];
        }
        return result;
    }
