class Solution {
public:
    int maxRotateFunction(vector<int>& nums) {

        //Actual Code Starts here
        int sum = 0, n = nums.size();
        int prev=0;
        for(int i=0;i<n;i++){
            sum+=nums[i];
            if(i<n-1)prev+=(i+1)*nums[i];
        }
        int ans = prev;
        for(int i = n-2; i>=0; i--){
            int cur = prev + sum - n*nums[i];
            prev = cur;
            ans = max(ans,prev);
        }
        return ans;

        //Space complexity O(1) and Time Compelexity O(N)
    }
};
```

