class Solution {
public:
    int maxRotateFunction(vector<int>& nums) {

        int n = nums.size();

        vector<int> d1(n, 0);
        d1[n-1] = nums[n-1];

        vector<int> d2(n, 0);
        d2[0] = nums[0];

        for(int i=n-2;i>=0;i--) {
            d1[i] = nums[i] + d1[i+1];
