public int maxRotateFunction(int[] nums) {
        int n = nums.length;
        int dp[] = new int[n];
        int sum = 0;
        for (int i=0;i< n;i++){
            dp[0] += nums[i] * i;
            sum += nums[i];
        }

        int max = dp[0];
        for (int i=1;i<n;i++){
            dp[i] = dp[i-1] + (sum - nums[n-i] - (nums[n-i] * (n-1)));
            max = Math.max(max,dp[i]);
        }

        return max;
    }
