class Solution {
public:
    int maxRotateFunction(vector<int>& A) {
        /*
        A = [4, 3, 2, 6]       
        F(0) = (0 * 4) + (1 * 3) + (2 * 2) + (3 * 6) = 0 + 3 + 4 + 18 = 25
        F(1) = (0 * 6) + (1 * 4) + (2 * 3) + (3 * 2) = 0 + 4 + 6 + 6 = 16
        F(2) = (0 * 2) + (1 * 6) + (2 * 4) + (3 * 3) = 0 + 6 + 8 + 9 = 23
        F(3) = (0 * 3) + (1 * 2) + (2 * 6) + (3 * 4) = 0 + 2 + 12 + 12 = 26
        --> F(1) = F(0) + Sum - 6 * (3+1) = F(0) + Sum - A[n-j] * n
        */
        int n = A.size();
        int ValCurr = 0, ValSum = 0;
        for(int i=0; i<n; i++){
            ValCurr += i * A[i];
            ValSum += A[i];
        }
        int j = 1, ValMax = ValCurr;
        while( j < n ){
            // update to the next
            ValCurr += ValSum - n * A[n-j];
            if(ValCurr > ValMax) ValMax = ValCurr;
            j++;
        }
        return ValMax;
    }
};
```

