        def findKth(self,A,B,k):
            if len(A)>len(B):
                A,B=B,A
            if not A:
                return B[k]
            if k==len(A)+len(B)-1:
                return max(A[-1],B[-1])
            i=len(A)//2
            j=k-i
            if A[i]>B[j]:
                #Here I assume it is O(1) to get A[:i] and B[j:]. In python, it's not but in cpp it is.
                return self.findKth(A[:i],B[j:],i)
            else:
                return self.findKth(A[i:],B[:j],j)

