    public int findKth(int[] nums1, int s1, int[] nums2, int s2, int k) {
        if (s1 >= nums1.length) {
            return nums2[s2 + k - 1];
        }    
        if (s2 >= nums2.length) {
            return nums1[s1 + k - 1];
        }
        if (k == 1) {
            return Math.min(nums1[s1], nums2[s2]);
        }
        int m1 = s1 + k / 2 - 1;
        int m2 = s2 + k / 2 - 1;
        int mid1 = 0;
        int mid2 = 0;
        if (m1 >= nums1.length) {
            mid1 = Integer.MAX_VALUE;
        }
        else {
            mid1 = nums1[m1];
        }
        if (m2 >= nums2.length) {
            mid2 = Integer.MAX_VALUE;
