      int x = (middle - 0.5).toInt();
      int y = (middle + 0.5).toInt();
      return (nums[x] + nums[y]) / 2;
    } else {
      int x = (middle - 0.5).toInt();;
      return nums[x].toDouble();
    }
  }
}
