```
class Solution
{
	public double findMedianSortedArrays(int[] A, int[] B) 
	{
        int m = A.length;
        int n = B.length;
		int start = 0;
		int end = m;

        if(m > n)
		{
            return findMedianSortedArrays(B, A);
        }

        while(start <= end)
        {
            int i = start + (end - start) / 2;
            int j = (m + n + 1) / 2 - i;

            double x1 = (i == 0) ? Integer.MIN_VALUE : A[i - 1];
            double y1 = (i == m) ? Integer.MAX_VALUE : A[i];
            double x2 = (j == 0) ? Integer.MIN_VALUE : B[j - 1];
            double y2 = (j == n) ? Integer.MAX_VALUE : B[j];

