class Solution {
public:
    double findMedianSortedArrays(vector<int>& nums1, vector<int>& nums2) {
        // Ensure nums1 is smaller or equal in size to nums2
        if (nums1.size() > nums2.size()) {
            return findMedianSortedArrays(nums2, nums1);
        }

        // Length of the merged array
        int m = nums1.size();
        int n = nums2.size();
        int total = m + n;

        // Binary search variables
        int left = 0;
        int right = m;
        int i, j;

        // Binary search to find partition indices
        while (left <= right) {
            i = (left + right) / 2;
            j = (total + 1) / 2 - i;

            if (i < m && nums2[j-1] > nums1[i]) {
                // i is too small, increase it
                left = i + 1;
            } else if (i > 0 && nums1[i-1] > nums2[j]) {
                // i is too big, decrease it
                right = i - 1;
            } else {
                // i is perfect
                int max_left;
                if (i == 0) {
                    max_left = nums2[j-1];
                } else if (j == 0) {
                    max_left = nums1[i-1];
                } else {
                    max_left = max(nums1[i-1], nums2[j-1]);
                }

                if (total % 2 == 1) {
                    // Odd length, median is in the middle
                    return max_left;
                }

                int min_right;
                if (i == m) {
                    min_right = nums2[j];
                } else if (j == n) {
                    min_right = nums1[i];
                } else {
                    min_right = min(nums1[i], nums2[j]);
                }

                // Even length, median is average of two middle elements
                return (max_left + min_right) / 2.0;
            }
        }

        // Should never reach here
        return -1;
    }
};

