class Solution:
    def findMedianSortedArrays(self, nums1: List[int], nums2: List[int]) -> float:
        m, n = len(nums1), len(nums2)
        medianIdx1 = (m + n) // 2
        medianIdx2 = (m + n - 1) // 2
        i = j = k = 0
        medianSum = 0
        while i < m or j < n:
            if j == n or i < m and nums1[i] < nums2[j]:
                cur = nums1[i]
                i += 1
            else:
                cur = nums2[j]
                j += 1
            if k == medianIdx1:
                medianSum += cur
            if k == medianIdx2:
                medianSum += cur
            k += 1

        return medianSum / 2
