class Solution:
    def findMedianSortedArrays(self, nums1: List[int], nums2: List[int]) -> float:
        m, n = len(nums1), len(nums2)
        if m > n: return self.findMedianSortedArrays(nums2, nums1)
        left = 0
        right = m
        while left <= right:
            partition1 = (left + right) // 2
            partition2 = (m + n + 1) // 2 - partition1
            left1 = -math.inf if partition1 == 0 else nums1[partition1-1]
            left2 = -math.inf if partition2 == 0 else nums2[partition2-1]
            right1 = math.inf if partition1 == m else nums1[partition1]
            right2 = math.inf if partition2 == n else nums2[partition2]
