        int n2=nums2.size();
        if(n2<n1) return findMedianSortedArrays(nums2,nums1);

        int low=0,high=n1;
        while(low<=high){
            int cut1=(high+low)/2;
            int cut2=(n1+n2+1)/2-cut1;

            int left1=cut1==0?INT_MIN:nums1[cut1-1];
            int left2=cut2==0?INT_MIN:nums2[cut2-1];

            int right1=cut1==n1?INT_MAX:nums1[cut1];
