        int totalLeft = (n+m+1)/2;

        int s=0, e=n;

        while(s<=e){
            int mid = (s+e)/2;
            int countA = mid;
            int countB = totalLeft-countA;

            double leftA = (countA==0) ? INT_MIN : A[countA-1] ;
            double rightA = (countA==n) ? INT_MAX : A[countA] ;

            double leftB = (countB==0) ? INT_MIN : B[countB-1] ;
