class Solution {
public:
    double findMedianSortedArrays(vector<int>& nums1, vector<int>& nums2) {
        int a = nums1.size(), 
            b = nums2.size();
        int n = a + b;
        if (n % 2 == 1) {
            return (double)kthElement(nums1, nums2, 0, a - 1, 0, b - 1, n / 2);
        }
        double sum = kthElement(nums1, nums2, 0, a - 1, 0, b - 1, n / 2) + 
            kthElement(nums1, nums2, 0, a - 1, 0, b - 1, n / 2 - 1);
        return sum / 2;
    }

    // kth index element in new array.
    int kthElement(vector<int>& nums1, vector<int>& nums2, int l1, int h1, int l2, int h2, int k) {
        if (l1 > h1) {
            return nums2[l2 + k];
        }

        if (l2 > h2) {
            return nums1[l1 + k];
        }

        // we have to divide in such a manner that in every iteration, we are reducing the size of array for us to leave with only one array in end
        // we divide such that mid elements are on brink of being kth element, i.e. in cases when first k elements lie in first half of both arrays,
        // the kth element could either be m1 or m2, giving us a chance to eliminate one of them, hence reducing the size
        int m1 = (l1 + h1) / 2,
            m2 = (l2 + h2) / 2,
            leftLen1 = m1 - l1,
            leftLen2 = m2 - l2;

        // Case 1 : kth index occurs in second half. So we can reduce the first half of nums1 or nums2. We discard the smaller half
        // Note here that while reducing we remove the mid element as well. While counting we didn't include mid elements in leftLen.
        // when we say k > leftlen, that means, we can discard all elements to left (only by numbers - k- 1 elements) + 1 of the mid elements
        // while still having kth element in new boundaries. 
        // Although, we don't know if kth element lies in smaller second half or first half, hence we just remove smaller first half.
        if (leftLen1 + leftLen2 < k) {
            if (nums1[m1] < nums2[m2]) {
                return kthElement(nums1, nums2, m1 + 1, h1, l2, h2, k - leftLen1 - 1);
            }

            return kthElement(nums1, nums2, l1, h1, m2 + 1, h2, k - leftLen2 - 1);
        }

        // Case 2: kth element is present in first half.
        // kth element can not be greater than m1 or m2, which means we can discard one of it.
        // discard greater second half
        if (nums1[m1] < nums2[m2]) {
            return kthElement(nums1, nums2, l1, h1, l2, m2 - 1, k);
        }

        return kthElement(nums1, nums2, l1, m1 - 1, l2, h2, k);
    }
};
