class Solution(object):
    def findMedianSortedArrays(self, A, B):
        num = len(A) + len(B)
        if num % 2 == 0:
            return (self.findKth(A, B, num//2 + 1) + 
                    self.findKth(A, B, num//2)) / 2.
        else:
            return self.findKth(A, B, num//2 + 1)

    def findKth(self, A, B, K):
        if not A:
            return B[K - 1]
        if not B:
            return A[K - 1]
        if K == 1:
            return min(A[0], B[0])
        step = min(K//2, min(len(A), len(B)))
        if A[step - 1]  < B[step - 1]:
            return self.findKth(A[step:], B, K - step)
        else:
            return self.findKth(A, B[step:], K - step)
