    double findMedianSortedArrays(vector<int>& nums1, vector<int>& nums2) {
        vector<int> ans;
        int i =0 , j =0;
        int size_num1 = nums1.size();
        int size_num2 = nums2.size();

        while(size_num1>i || size_num2>j){
             ans.push_back(nums1[i]);
                i++;
        }
        while(size_num2>j){
             ans.push_back(nums2[j]);
               j++;
        }
        sort(ans.begin(),ans.end());
        int ans_size = ans.size();
        float mid = ans_size/2;
        if (ans_size % 2 == 0){
            float median = float (ans[mid]+ans[mid-1])/2;
            // double a1 = double(ab)/2;
            return median;
        }
        else{
            return ans[mid];
        }
    }
};

