class Solution {
    public double findMedianSortedArrays(int[] nums1, int[] nums2) {
     int l1=nums1.length;
    int l2=nums2.length;
    int c=(l1+l2)/2;
    c++;
    double m=0, n=0;
    boolean odd=true;
    if((l1+l2)%2==0){
      odd=false;
    }
    int p1=0, p2=0;
    while(c-->0){
      n=m;
      int p1v=getp(nums1, p1);
      int p2v=getp(nums2, p2);
      if(p1v<p2v){
        m=nums1[p1++];
      }
      else{
        m=nums2[p2++];
      }
    }
    if(!odd){
      return new Double ((m+n)/2);
    }
    return m;
    }
    public static int getp(int [] db, int p){
    if(p>=db.length){
      return Integer.MAX_VALUE;
    }
    return db[p];
  }
}

