class Solution {
    public double findMedianSortedArrays(int[] nums1, int[] nums2) {
        int arr[] = new int[nums1.length+nums2.length];
        int j = 0;
        for(int i=0; i<nums1.length; i++) {
            arr[j++] = nums1[i];
        }
        for(int i:nums2) {
            arr[j++] = i;
        }
        Arrays.sort(arr);
        ArrayList<Integer> list = new ArrayList<>();
        int size = arr.length;
        if(size % 2 == 0) {
            list.add(arr[size/2]);
            list.add(arr[(size-1)/2]);
        }
        else {
            list.add(arr[size/2]);
        }
        int sum =0;
        for(int i:list) {
            sum =sum +i;
        }

        return (double) sum/list.size();
    }
}

