```
	unordered_map<int,int> hashMap;
	bool isPossible(vector<int>& stones, int currIdx, int steps){

		if(hashMap.find(stones[currIdx] + steps) == hashMap.end() or steps <= 0)
			return false;

		// shifting current index to stone position that can be reachable
		currIdx = hashMap[stones[currIdx] + steps];

		if(currIdx == stones.size() - 1)
			return true;

		bool oneStepBack = isPossible(stones, currIdx, steps - 1);
		bool sameSteps = isPossible(stones, currIdx, steps);
		bool oneStepForward = isPossible(stones, currIdx, steps + 1);

		return oneStepBack or sameSteps or oneStepForward;

	}
	bool canCross(vector<int>& stones) {
		for(int i = 0; i < stones.size(); i++)
			hashMap[stones[i]] = i;
		return isPossible(stones, 0, 1);
	}
