class Solution {
public:
    int cal(int i,int k,vector<int>&v,unordered_map<int,int>&index,int dp[][1100]){
        if(i+1==v.size())
            return 1;

        if(dp[i][k]!=-1)
            return dp[i][k];

        int x=0,y=0,z=0;

        if(k-1>0 and index.count(v[i]+k-1)>0)
            x=cal(index[v[i]+k-1],k-1,v,index,dp);

        if(x==0 and index.count(v[i]+k)>0)
            y=cal(index[v[i]+k],k,v,index,dp);

        if(x==0 and y==0 and index.count(v[i]+k+1)>0)
            z=cal(index[v[i]+k+1],k+1,v,index,dp);

        dp[i][k]=(x|y)|z;

        return dp[i][k];
    }

    bool canCross(vector<int>&v) {
        unordered_map<int,int>index;
        for(int i=0;i<v.size();i++)
            index[v[i]]=i;
        int dp[1100][1100];
        memset(dp,-1,sizeof(dp));
        if(v[1]==1 and cal(1,1,v,index,dp))
            return true;
        return false;
    }
};
