class Solution {
    private int n;
    private List<Integer> jumps = Arrays.asList(-1, 0, 1);
    private Map<String, Integer> hashMap = new HashMap<>();

    private boolean solve(List<Integer> nums, int i, int prev) {
        if (i >= n) {
            return false;
        }
        if (i == n - 1) {
            return true;
        }
        String key = i + "_" + prev;
        if (hashMap.containsKey(key)) {
            return hashMap.get(key) == 1;
        }
        int curr = nums.get(i);
        boolean isPossible = false;
        for (int t : jumps) {
            int jump = prev + t;
            for (int j = i + 1; j < n; j++) {
                if (curr + jump == nums.get(j)) {
                    isPossible = solve(nums, j, jump);
                    if (isPossible) {
                        hashMap.put(key, 1);
                        return true;
                    }
                }
            }
        }
        hashMap.put(key, 0);
        return false;
    }

    public boolean canCross(List<Integer> stones) {
        if (stones.get(1) > 1) {
            return false;
        }
        n = stones.size();
        hashMap.clear();
        return solve(stones, 1, 1);
    }
}
