```cpp []
class Solution {
private:
    int n;
    vector<int> jumps = {-1, 0, 1};
    unordered_map<string, int> hashMap;
    int binarySearch(vector<int>& nums, int l, int r, int key)
    {
        int mid;
        while(l <= r)
        {
            mid = l + (r-l)/2;
            if(key < nums[mid])
            {
                r = mid-1;
            }
            else if(key > nums[mid])
            {
                l = mid+1;
            }
            else
            {
                return mid;
            }
        }
        return -1;
    }
    bool solve(vector<int>& nums, int i, int prev)
    {
        if(i >= n)
        {
            return false;
        }
        if(i == n-1)
        {
            return true;
        }
        string key = to_string(i) + "_" + to_string(prev);
        if(hashMap.count(key))
        {
            return hashMap[key];
        }
        int curr = nums[i];
        for(int& t: jumps)
        {
            int jump = prev + t;
            int j = binarySearch(nums, i+1, n-1, curr + jump);
            if(j == -1)
            {
                continue;
            }
            if(solve(nums, j, jump))
            {
                return true;
            }
        }
        return hashMap[key] = false;
    }
public:
    bool canCross(vector<int>& stones) {
        if(stones[1] > 1)
        {
            return false;
        }
        n = stones.size();
        hashMap.clear();
        return solve(stones, 1, 1);
    }
};
