```python []
class Solution:
    def __init__(self):
        self.n = 0
        self.jumps = [-1, 0, 1]
        self.hashMap = {}

    def binarySearch(self, nums, l, r, key):
        while l <= r:
            mid = l + (r - l) // 2
            if key < nums[mid]:
                r = mid - 1
            elif key > nums[mid]:
                l = mid + 1
            else:
                return mid
        return -1

    def solve(self, nums, i, prev):
        if i >= self.n:
            return False
        if i == self.n - 1:
            return True
        key = str(i) + "_" + str(prev)
        if key in self.hashMap:
            return self.hashMap[key]
        curr = nums[i]
        for t in self.jumps:
            jump = prev + t
            j = self.binarySearch(nums, i + 1, self.n - 1, curr + jump)
            if j == -1:
                continue
            if self.solve(nums, j, jump):
                return True
        self.hashMap[key] = False
        return False

    def canCross(self, stones):
        if stones[1] > 1:
            return False
        self.n = len(stones)
        self.hashMap = {}
        return self.solve(stones, 1, 1)
