class Solution {
public:
    map<int,int> mp;
    bool canCross(vector<int>& st) {
        int n = st.size();
        vector<vector<int>> dp(n,vector<int> (2*n,-1));
        for(int i = 1 ; i < n ; i++){
            mp[st[i]] = i ;
        }
        if(st[1] - st[0] > 1){
            return false;
        }
        return f(1,1,st,dp);
    }
    bool f(int i , int k, vector<int>& st,vector<vector<int>>&dp){
        if(i == st.size() - 1){
            return true;
        }
        bool bk = false;
        bool atk = false;
        bool ak = false;
        if(dp[i][k] !=-1){
            return dp[i][k];
        }
        <!-- For k - 1 -->
        if(mp[st[i] + k-1] > i){
            bk = f(mp[st[i] + k-1],k-1,st,dp);
        }
        <!-- For k -->
        if(mp[st[i] + k] > i){
            atk = f(mp[st[i] + k],k,st,dp);
        }
        <!-- For k + 1 -->
        if(mp[st[i] + k+1] > i){
            ak = f(mp[st[i] + k+1],k+1,st,dp);
        }
        return dp[i][k] = (bk || atk || ak);
    }
};
```

