        }
        Map<Integer,Integer> map = new HashMap<>();
        for(int i=0;i<n;i++){
            map.put(stones[i],i);
        }
        Boolean[][] memo = new Boolean[n][2000];
        return solve(stones,1,n,1,map,memo);
    }
    private boolean solve(int[] stones, int index, int n, int k,Map<Integer,Integer> map,Boolean[][] memo){
        if(index==n-1){
            return true;
        }
        if(memo[index][k]!=null){
            return memo[index][k];
        }
        boolean x=false,y=false,z=false;
        if(k>1 && map.containsKey(stones[index]+k-1)){
