# Code
```
class Solution {
private:
    bool frogCrosses(vector<int>& stones, int jump, int idx, int n, vector<vector<int>> &dp, unordered_map<int, int> &sIdx){

        //base case agr hm last index pr hai toh return true
        if(idx == n-1)
            return true;

        //kya current index se jump krne pr hm last index tk phch paege? -1 = Pata Nhi; 0 = NO; 1 = Yes
        if(dp[idx][jump] != -1)
            return dp[idx][jump];

        //agr jump 0 hai toh infinte loop me aajaege isliye return 0 - means nhi phch paege
        if(jump == 0)
            return dp[idx][jump] = 0;

        //save stone value jha jump krne ke baad phchege
        int stoneVal = jump + stones[idx];
        int newIdx = -1;

        //find index of stone value in stones array using stone-index map 
        if(sIdx[stoneVal] != 0)
            newIdx = sIdx[stoneVal];
        else
            return dp[idx][jump] = 0;

        //call kro saare possible jumps ke liye
        if(newIdx <= n-1){
            bool j1 = frogCrosses(stones, jump-1, newIdx, n, dp, sIdx);
            bool j2 = frogCrosses(stones, jump, newIdx, n, dp, sIdx);
            bool j3 = frogCrosses(stones, jump+1, newIdx, n, dp, sIdx);
            return dp[idx][jump] = j1 | j2 | j3;
        }

        return dp[idx][jump] = 0;
    }

public:
    bool canCross(vector<int>& stones) {

        int n = stones.size();

        //Create stone-index map for fast search of index location - O(1) time Complexity me
        unordered_map<int, int>sIdx;
        for(int i=0; i<n; i++){
            sIdx[stones[i]] = i;
        }

        //dp[i][j] which stores if I make jump 'j' from index 'i'
        //then: if dp[i][j] = 0 means nhi phch paege n-1(last) index tk
        //     if dp[i][j] = 1 means phch paege n-1(last) index tk
        //     if dp[i][j] = -1 means abhi pta nhi hai
        vector<vector<int>> dp(n, vector<int>(n, -1));
        return frogCrosses(stones, 1, 0, n, dp, sIdx);
    }
};
