class Solution {
public:
    bool ans = false;
    // 'ans' just to check if we reached the last index, not necessary to use this
    //max jump can reach ~2001 (1+1+1+1+....)
    // let states be prev INDEX (not position) and cur jump 
    // here prev denotes the previous index we reached
    // and jump denotes the jump from 'prev'
    // we check if stones[prev]+(jump) exists in the stones array 
    //if it is present we check the next states
    //time complexity (n^2)log n , space n^2
    vector<vector<int>>dp{2010,vector<int>(2010,-1)};

    //recursive function
    int rec(int jump, int prev,vector<int>&stones){
        int n  = stones.size();
        if(jump+ stones[prev]==stones[n-1]){dp[prev][jump]=1;ans =true;return 1;}
        // answer =true, we reached the last index
        if(jump + stones[prev]> stones [n-1])return 0  ;
        // jumped more than the last position 

        if(dp[prev][jump]!=-1)return dp[prev][jump];
        // checking if we already calculated this state
        if(ans)return 1;// not necessary just to reduce some function calls 


        int cur = jump+stones[prev];// this denotes what would be the position after we jump 'jump' from prev index
        int x = lower_bound(stones.begin(),stones.end(),cur)-stones.begin();
        // check if that position exists in the stones array-> binary search
        if(x==n  )return 0;//position not present
        if(stones[x]!=cur)return 0;// position not present

        //position present now check for the next possible states with jump,jump+1,jump-1
        bool ans =false;
        ans  |=rec(jump,x,stones); 
       if(jump>1)ans |=rec(jump-1,x,stones);//a jump of 0 will cause TLE
        ans  |=rec(jump+1,x,stones);
      return dp[prev][jump]=ans;
    }


    bool canCross(vector<int>& stones) {
        if(stones[1]!=1)return false;

         rec(1,0,stones);//start index 0, start jump 1
        return ans ;
    }
};
