class Solution {
    HashMap<Integer,Boolean> map;
    int[] arr;
    HashMap<String,Boolean> dp;
    public boolean canCross(int[] stones) {
        map = new HashMap<>();
        arr = stones;
        dp = new HashMap<>();
        for(int val:stones){
            map.put(val,true);
        }
        return helper(1,0);
    }
    public boolean helper(int jump,int curPos){
        if(curPos == arr[arr.length-1]){
            return true;
        }
        if(jump<1 || !map.containsKey(curPos)){
            return false;
        }
        if(dp.containsKey(jump+"#"+curPos)){
            return dp.get(jump+"#"+curPos);
        }
        boolean res = helper(jump-1,curPos+jump) ||  helper(jump,curPos+jump) ||  helper(jump+1,curPos+jump);
        dp.put(jump+"#"+curPos,res);
        return res;
    }
}
