class Solution {
    public boolean canCross(int[] stones) {
        Queue<int[]> q = new LinkedList<>();
        q.offer(new int[]{0, 0});
        boolean[][] seen = new boolean[1100][1100];
        seen[0][0] = true;
        HashMap<Integer, Integer> map = new HashMap<>();
        for (int i = 0; i < stones.length; i++) map.put(stones[i], i);
        while (!q.isEmpty()) {
            int[] top = q.poll();
            int k = top[1];
            int index = top[0];
            if (index == stones.length - 1) return true;
            for (int i = -1; i <= 1; i++) {
                int newK = k + i;
                int newPos = stones[index] + newK;
                int newIndex = map.getOrDefault(newPos, -1);
                if (newIndex > index && !seen[newIndex][newK]) {
                    seen[newIndex][newK] = true;
                    q.offer(new int[]{newIndex, newK});
                }
            }
        }
        return false;
    }
}
