class Solution {
public:
    bool canCross(vector<int>& stones) {
        // Creating haspmap for storing differnt positions where frog can go
        unordered_map<int,unordered_set<int>> mp;
        // Initialising map
        mp[stones[0]+1]={1};
        for(int i=1;i<stones.size();i++)
        {
            for(auto jump: mp[stones[i]])
            {
                // jump to k
                mp[stones[i]+jump].insert(jump);
                // jump to k-1
                mp[stones[i]+jump-1].insert(jump-1);
                // jump to k+1
                mp[stones[i]+jump+1].insert(jump+1);
            }
        }
        // checking and returning of last index whether frog reached or not
        return mp[stones[stones.size()-1]].size()!=0;
    }
};
