class Solution {
public:
    int pos(int ind,vector<int>&stones,int steps){
        for(int i=ind+1;i<stones.size();i++){
            if(stones[i]-stones[ind]==steps){
                return i;
            }
            if(stones[i]-stones[ind]>steps)return -1;
        }
        return -1;
    }
    bool solve(int ind,vector<int>&stones,int prev,map<pair<int,int>,int>&dp){
        if(ind>=stones.size()-1){
            if(ind==stones.size()-1){
                dp[{ind,prev}]=2;
                return 1;
            }
            return 0;
        }
        if(dp[{ind,prev}]!=0)return dp[{ind,prev}]-1;
        int s1=prev-1,s2=prev,s3=prev+1;
        if(s1>0){
            int i=pos(ind,stones,s1);
            if(i!=-1){
                if(solve(i,stones,s1,dp)){
                    dp[{ind,prev}]=2;
                    return 1;
                }
            }
        }
        if(s2>0){
            int i=pos(ind,stones,s2);
            if(i!=-1){
                if(solve(i,stones,s2,dp)){
                    dp[{ind,prev}]=2;
                    return 1;
                }
            }
        }
        if(s3>0){
            int i=pos(ind,stones,s3);
            if(i!=-1){
                if(solve(i,stones,s3,dp)){
                    dp[{ind,prev}]=2;
                    return 1;
                }
            }
        }
        dp[{ind,prev}]=1;
        return 0;
    }
    bool canCross(vector<int>& stones) {
        map<pair<int,int>,int>dp;
        return solve(0,stones,0,dp);
    }
};
