```C++
class Solution {
public:
    int dp[2001][2001];
    unordered_map<int, int> h;
    bool solve(vector<int> &stones, int stoneIndex, int k) {
        int n = stones.size();
        // case where frog reaches the last stoneIndex
        if (stoneIndex == n - 1) return true;

        // cases where frog jumps out of boundary
        if (stoneIndex >= n) return false;

        // cases that we have already visited
        if (dp[stoneIndex][k] != -1) return dp[stoneIndex][k];

        // curr stone position
        int currStone = stones[stoneIndex];

        // from here we can go either jump k, k-1 or k+1 unit
        bool ans = false;
        for (int step = -1; step <= 1; step++) {
            int nextJump = k + step;
            if (nextJump > 0) {
                // next stone where we may jump
                int nextStone = currStone + nextJump;
                // checking whether the position or stone exist in the array or not
                if (h.count(nextStone) != 0) {
                    // next stone index
                    int nextJumpIndex = h[nextStone];

                    // now jumping to the next stone
                    ans = ans || solve(stones, nextJumpIndex, nextJump);
                }
            }
        }
        dp[stoneIndex][k] = ans;
        return ans;
    }
    bool canCross(vector<int>& stones) {
        for (int i = 0; i < stones.size(); i++) {
            h.insert({stones[i], i});
        }
        memset(dp, -1, sizeof(dp));
        return solve(stones, 0, 0);
    }
};
```

