class Solution {
    unordered_map<int, int> um;
    bool dfs(vector<int>& stones, int i, int k, vector<vector<int>> &dp)
    {
        if(i == stones.size()-1)
            return true;
        if(dp[i][k] != -1)
            return dp[i][k];
        bool val = false;
        if(i == 0)
        {
            if(um.count(stones[i]+k+1))
